  // Show pending indicator
  setPending(true);

  console.log(data);

  contact.submit(data)
    .then(() => {
      // Clear form
      reset();
      // Show success alert message
      setFormAlert({
        type: "success",
        message: "Your message has been sent!",
      });
    })
    .catch((error) => {
      // Show error alert message
      setFormAlert({
        type: "error",
        message: error.message,
      });
    })
    .finally(() => {
      // Hide pending indicator
      setPending(false);
    });
};

